__doc__ = "Update portal template(s) content with initial image"

import sys
import os

# Django setup part
sys.path.append('/home/vlt-os/vulture_os')
os.environ.setdefault("DJANGO_SETTINGS_MODULE", 'vulture_os.settings')

import django
from django.conf import settings
django.setup()

from system.cluster.models import Cluster, Node

if not Cluster.is_node_bootstrapped():
    sys.exit(0)
from authentication.portal_template.models import PortalTemplate, TemplateImage

if __name__ == "__main__":

    node = Cluster.get_current_node()
    if not node:
        print("Current node not found. Maybe the cluster has not been initiated yet.")
    else:
        try:
            #
            new_image = None
            try:
                image_1 = TemplateImage.objects.get(pk=1)
                new_image = TemplateImage.objects.create(name=image_1.name, image_type=image_1.image_type, content=image_1.content)
                image_1.delete()
            except Exception as e:
                pass
            # Create vulture-logo ID as 1
            logo = TemplateImage(id=1, name="vulture-logo.png", image_type="image/png", content="")
            logo.save()
            #
            for template in PortalTemplate.objects.all():
                for attr in dir(template):
                    if not attr.startswith("html_"):
                        continue
                    html_content = getattr(template, attr)
                    if new_image:
                        html_content = html_content.replace("{{image_1}}", "{{{{image_{}}}}}".format(new_image.id))
                    html_content = html_content.replace("<img id=\"vulture_img\" src=\"/templates/static/img/vulture-logo-small.png\"/>", "<img id=\"vulture_img\" src=\"{{image_1}}\"/>")
                    setattr(template, attr, html_content)
                template.save()
                print("Portal template {} updated".format(template))
        except Exception as e:
            print("Failed to update portal templates: {}".format(e))
            print("Please relaunch this script after solving the issue.")

        print("Done.")
